import React, { useState } from "react";
import { employmentRegionData } from "../data/employmentRegionData";

const RegionSelector = () => {
  const [selectedSido, setSelectedSido] = useState("");
  const [selectedGugun, setSelectedGugun] = useState("");

  const handleSidoChange = (e) => {
    setSelectedSido(e.target.value);
    setSelectedGugun(""); // 초기화
  };

  const handleGugunChange = (e) => {
    setSelectedGugun(e.target.value);
  };

  const selectedRegion = employmentRegionData.find((r) => r.시도 === selectedSido);

  return (
    <div>
      <h2>고용이력부</h2>
      <div style={{ marginBottom: "1rem" }}>
        <label>시도 선택: </label>
        <select value={selectedSido} onChange={handleSidoChange}>
          <option value="">-- 선택 --</option>
          {employmentRegionData.map((region) => (
            <option key={region.시도} value={region.시도}>
              {region.시도}
            </option>
          ))}
        </select>
      </div>

      {selectedSido && (
        <div>
          <label>구군 선택: </label>
          <select value={selectedGugun} onChange={handleGugunChange}>
            <option value="">-- 선택 --</option>
            {selectedRegion.구군목록.map((g) => (
              <option key={g.구군} value={g.구군}>
                {g.구군} ({g.업체수.toLocaleString()}개)
              </option>
            ))}
          </select>
        </div>
      )}
    </div>
  );
};

export default RegionSelector;
